mapper.select:
select prod_no ,prod_name ,prod_price ,prod_category ,prod_image1 ,prod_image2 ,prod_image3 ,prod_image4 ,prod_exp ,prod_code ,thumbnail ,prod_category2 ,prod_star FROM product where 1=1
and prod_category = #{prodCategory} and prod_category2 = #{prodCategory2}
and prod_star = #{prodStar}
ORDER BY prod_no

		SELECT b.*
		FROM
		(
		SELECT rownum AS rn, a.*
		FROM (
		      SELECT *
		      FROM product
		      where 1=1
		         
			  and prod_category = #{prodCategory}
			  and prod_category2 = #{prodCategory2}
		      
		      
			  and prod_star = #{prodStar}
		      
		      ORDER BY prod_no DESC
		     ) a
		) b
		WHERE b.rn> (#{page} -1 ) * 12
        
        AND b.rn <= (#{page} * 12)

select count(1)
from product    
WHERE 1=1

	and prod_category = #{prodCategory}
	and prod_category2 = #{prodCategory2}


	and prod_star = #{prodStar}
